// Check if this contact was recently synced to avoid loops
const items = $input.all();
const filteredItems = [];

for (const item of items) {
  const contact = item.json;
  const properties = contact.properties || {};

  // Check if this was recently synced from HubSpot
  const hubspotSyncTime = properties['Hubspot Sync Timestamp']?.rich_text?.[0]?.plain_text;

  if (hubspotSyncTime) {
    const syncTime = new Date(hubspotSyncTime);
    const now = new Date();
    const timeDiff = now - syncTime;

    // Skip if synced from HubSpot within last 2 minutes (avoid loops)
    if (timeDiff < 2 * 60 * 1000) {
      console.log(`Skipping contact ${contact.id} - recently synced from HubSpot`);
      continue;
    }
  }

  // Check if this contact has a valid email or phone (required for HubSpot)
  const email = properties['Email']?.email;
  const phone = properties['Phone Number']?.phone_number;

  if (email || phone) {
    filteredItems.push(item);
  } else {
    console.log(`Skipping contact ${contact.id} - no email or phone`);
  }
}

return filteredItems;

// Transform Notion contact data to HubSpot format
const notionData = $input.first().json;

// Property mapping based on our analysis
const propertyMapping = {
  'Email': 'email',
  'Access Point Location': 'access_point_location',
  'Marketing contact status': 'hs_marketable_status',
  'Address': 'address',
  'Phone Number': 'phone',
  'Shipping Profile': 'shipping_profile',
  'Re-Engagement Notes': 'reengagementnotes',
  'Lifecycle Stage': 'lifecyclestage',
  'Contact Profile': 'contact_type',
  'Country/Region': 'country',
  'Sales Status': 'hs_lead_status',
  'Order Email Text': 'order_email_text',
  'Name': 'firstname', // Will need special handling for name splitting
  'Id': 'notion_contact_id', // Custom field to track Notion ID
  'Hubspot Contacts Record ID': 'hs_object_id'
};

// Extract properties from Notion format
const notionProperties = notionData.properties || {};
const hubspotProperties = {};

// Map each property
for (const [notionProp, hubspotProp] of Object.entries(propertyMapping)) {
  if (notionProperties[notionProp]) {
    const notionValue = notionProperties[notionProp];
    let hubspotValue = null;

    // Handle different Notion property types
    switch (notionValue.type) {
      case 'email':
        hubspotValue = notionValue.email;
        break;
      case 'phone_number':
        hubspotValue = notionValue.phone_number;
        break;
      case 'rich_text':
        hubspotValue = notionValue.rich_text?.map(rt => rt.plain_text).join('') || '';
        break;
      case 'title':
        hubspotValue = notionValue.title?.map(t => t.plain_text).join('') || '';
        break;
      case 'select':
        hubspotValue = notionValue.select?.name || '';
        break;
      case 'multi_select':
        hubspotValue = notionValue.multi_select?.map(ms => ms.name).join(';') || '';
        break;
      case 'number':
        hubspotValue = notionValue.number?.toString() || '';
        break;
      case 'unique_id':
        hubspotValue = notionValue.unique_id?.prefix + notionValue.unique_id?.number || '';
        break;
      default:
        console.log(`Unhandled property type: ${notionValue.type} for ${notionProp}`);
    }

    if (hubspotValue !== null && hubspotValue !== '') {
      hubspotProperties[hubspotProp] = hubspotValue;
    }
  }
}

// Special handling for Name field - split into firstname and lastname
if (hubspotProperties.firstname) {
  const fullName = hubspotProperties.firstname;
  const nameParts = fullName.trim().split(' ');
  if (nameParts.length > 1) {
    hubspotProperties.firstname = nameParts[0];
    hubspotProperties.lastname = nameParts.slice(1).join(' ');
  }
}

// Add sync metadata
hubspotProperties.notion_sync_timestamp = new Date().toISOString();
hubspotProperties.notion_page_id = notionData.id;

// Get existing HubSpot ID if available
const hubspotId = hubspotProperties.hs_object_id || null;

return {
  json: {
    properties: hubspotProperties,
    notion_id: notionData.id,
    hubspot_id: hubspotId,
    has_hubspot_id: !!hubspotId
  }
};

// Transform HubSpot contact data to Notion format
const hubspotData = $input.first().json;

// Reverse property mapping
const propertyMapping = {
  'email': 'Email',
  'access_point_location': 'Access Point Location',
  'hs_marketable_status': 'Marketing contact status',
  'address': 'Address',
  'phone': 'Phone Number',
  'shipping_profile': 'Shipping Profile',
  'reengagementnotes': 'Re-Engagement Notes',
  'lifecyclestage': 'Lifecycle Stage',
  'contact_type': 'Contact Profile',
  'country': 'Country/Region',
  'hs_lead_status': 'Sales Status',
  'order_email_text': 'Order Email Text',
  'firstname': 'Name', // Will combine with lastname
  'lastname': 'Name',
  'notion_contact_id': 'Id',
  'hs_object_id': 'Hubspot Contacts Record ID'
};

// Extract properties from HubSpot format
const hubspotProperties = hubspotData.properties || {};
const notionProperties = {};

// Combine firstname and lastname
let fullName = '';
if (hubspotProperties.firstname || hubspotProperties.lastname) {
  fullName = [hubspotProperties.firstname, hubspotProperties.lastname]
    .filter(Boolean)
    .join(' ');
}

// Map each property
for (const [hubspotProp, notionProp] of Object.entries(propertyMapping)) {
  if (hubspotProperties[hubspotProp] !== undefined) {
    const hubspotValue = hubspotProperties[hubspotProp];
    let notionValue = null;

    // Handle special cases
    if (hubspotProp === 'firstname' || hubspotProp === 'lastname') {
      if (fullName && !notionProperties['Name']) {
        notionProperties['Name'] = {
          title: [{
            type: 'text',
            text: { content: fullName },
            plain_text: fullName
          }]
        };
      }
      continue;
    }

    // Convert to appropriate Notion format
    if (hubspotValue !== null && hubspotValue !== '') {
      switch (notionProp) {
        case 'Email':
          notionProperties[notionProp] = {
            email: hubspotValue
          };
          break;
        case 'Phone Number':
          notionProperties[notionProp] = {
            phone_number: hubspotValue
          };
          break;
        case 'Hubspot Contacts Record ID':
          notionProperties[notionProp] = {
            number: parseInt(hubspotValue) || 0
          };
          break;
        case 'Id':
          notionProperties[notionProp] = {
            unique_id: {
              prefix: 'CON',
              number: parseInt(hubspotValue) || 0
            }
          };
          break;
        case 'Marketing contact status':
        case 'Lifecycle Stage':
        case 'Country/Region':
        case 'Order Email Text':
        case 'Sales Status':
          notionProperties[notionProp] = {
            select: {
              name: hubspotValue
            }
          };
          break;
        case 'Contact Profile':
        case 'Shipping Profile':
          notionProperties[notionProp] = {
            multi_select: hubspotValue.split(';').map(item => ({
              name: item.trim()
            }))
          };
          break;
        default:
          // Default to rich_text
          notionProperties[notionProp] = {
            rich_text: [{
              type: 'text',
              text: { content: hubspotValue.toString() },
              plain_text: hubspotValue.toString()
            }]
          };
      }
    }
  }
}

// Add sync metadata
notionProperties['Hubspot Sync Timestamp'] = {
  rich_text: [{
    type: 'text',
    text: { content: new Date().toISOString() },
    plain_text: new Date().toISOString()
  }]
};

// Get existing Notion page ID if available
const notionId = hubspotProperties.notion_page_id || null;

return {
  json: {
    properties: notionProperties,
    hubspot_id: hubspotData.id,
    notion_id: notionId,
    has_notion_id: !!notionId
  }
};

// Update Notion contact with HubSpot ID for future syncs
const syncResult = $input.first().json;
const hubspotId = syncResult.id;
const notionId = syncResult.notion_id;

if (hubspotId && notionId) {
  // Return data to update the Notion contact with HubSpot ID
  return {
    json: {
      notion_id: notionId,
      properties: {
        'Hubspot Contacts Record ID': {
          number: parseInt(hubspotId)
        }
      }
    }
  };
}

return { json: {} };
